function m(l){const s=document.querySelector(".block-calculation__form");s.addEventListener("submit",t=>{t.preventDefault();const n=new FormData(s).get("numOfImages");if(!n)return;const e=c(n),a=document.querySelector(".calculation__manual_result"),o=document.querySelector(".calculation__ai_result");a.textContent=e.manual,o.textContent=e.ai});function c(t){const e=5*t,a=180*t,o=(e/3600).toFixed(1),u=(a/3600).toFixed(1);return{ai:o,manual:u}}}export{m as default};
